import { useState, useRef } from "react";
import styles from "../styles/carousel.module.css";

const Carousel = ({ slides }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [startX, setStartX] = useState(null);
  const sliderRef = useRef(null);

  const goToPrevious = () => {
    const isFirstSlide = currentIndex === 0;
    const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const isLastSlide = currentIndex === slides.length - 1;
    const newIndex = isLastSlide ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const goToSlide = (slideId) => {
    setCurrentIndex(slideId);
  };

  const handleTouchStart = (e) => {
    setStartX(e.touches[0].clientX);
  };

  const handleTouchMove = (e) => {
    if (!startX) {
      return;
    }
    const xDiff = startX - e.touches[0].clientX;

    if (xDiff > 50) {
      goToNext();
    } else if (xDiff < -50) {
      goToPrevious();
    }

    setStartX(null);
  };

  return (
    <div
      className={styles.slider_container}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      ref={sliderRef}
    >
      <div onClick={goToPrevious} className={styles.left_arrow}>
        ←
      </div>
      <div onClick={goToNext} className={styles.right_arrow}>
        →
      </div>
      <div
        className={styles.image_slider}
        style={{ backgroundImage: `url(${slides[currentIndex].url})` }}
      ></div>
      <div className={styles.dot_container}>
        {slides.map((slide, slidesid) => (
          <span
            onClick={() => {
              goToSlide(slidesid);
            }}
            className={styles.dot_styles}
            key={slidesid}
          >
            *
          </span>
        ))}
      </div>
    </div>
  );
};

export default Carousel;
