import styles from "../styles/collection.module.css";
import { useParams } from "react-router-dom";
import collections from "../Data.json";

const Collection = ({ name, headline, image, id }) => {
  const params = useParams();
  const collectionId = params.collectionId; // Get the collection ID from URL parameter

  // Find the collection object with the matching ID
  const collection = collections.collection.find((c) => c.id === parseInt(collectionId));

  if (!collection) {
    return <div>Collection not found.</div>;
  }

  return (
    <main className={styles.container}>
      <img src={collection.image} className={styles.img} alt={collection.name} />
      <div className={styles.collection_name}>
        <h2>{collection.name}</h2>
        <h3>{collection.headline}</h3>
      </div>
    </main>
  );
};

export default Collection;
