import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

import { EffectCoverflow, Pagination, Navigation } from 'swiper/modules';
import styles from "../styles/carosuel.module.css";

const Carousel = ({ slides }) => {
	return (
		<div className={styles.container}>
			<h1 className={styles.heading}>Flower Gallery</h1>
			<Swiper
				effect={"coverflow"}
				grabCursor={true}
				centeredSlides={true}
				loop={true}
				slidesPerView={"auto"}
				coverflowEffect={{
					rotate: 0,
					stretch: 0,
					depth: 100,
					modifier: 2.5,
				}}
				pagination={{ el: ".swiper-pagination", clickable: true }}
				navigation={{
					nextEl: ".swiper-button-next",
					prevEl: ".swiper-button-prev",
					clickable: true,
				}}
				modules={[EffectCoverflow, Pagination, Navigation]}
				className={styles.swiper_container}
			>
				{slides.map((image) => {
					<SwiperSlide>
						<img id={image.id} src={image.url} alt={image.title} />
					</SwiperSlide>;
				})}

				<div className={styles.slider_controler}>
					<div className={`${swiper_button_prev} ${styles.slider_arrow}`}>
						<ion-icon name="arrow-back-outline"></ion-icon>
					</div>
					<div className={`${swiper_button_next} ${styles.slider_arrow}`}>
						<ion-icon name="arrow-forward-outline"></ion-icon>
					</div>
					<div className={styles.swiper_pagination}></div>
				</div>
			</Swiper>
		</div>
	);
};

export default Carousel;
